mapper.select:
SELECT 

FROM wy_flea_market a

WHERE a.id = #{id}

SELECT 

FROM wy_flea_market a


	a.source = #{source}

		AND a.wyid = #{wyid}


		AND a.wyid IN (${wyids})


		AND a.goods_name LIKE CONCAT('%',#{goodsName},'%')


		AND a.source = #{source}


		AND a.group_id = #{groupId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC
