mapper.select:
SELECT * FROM vie_sys_dict WHERE del_flag = #{DEL_FLAG_NORMAL}
AND type = #{type}
AND description LIKE '%'||#{description}||'%' '%'+#{description}+'%' CONCAT('%', #{description}, '%')
ORDER BY type, code, update_date DESC

mapper.update:
UPDATE vie_sys_dict SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
