mapper.select:
SELECT 

FROM wx_text a

WHERE a.id = #{id}

SELECT
FROM wx_text a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.account_id = #{account.id}
AND a.keyword LIKE '%'||#{keyword}||'%' '%'+#{keyword}+'%' concat('%',#{keyword},'%')
AND a.account_id IN(${accountIds})
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM wx_text a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.account_id = #{account.id}


		AND a.keyword LIKE 
			'%'||#{keyword}||'%'
			'%'+#{keyword}+'%'
			concat('%',#{keyword},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE wx_text SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
