mapper.select:
SELECT 

FROM wy_biz_def a

WHERE a.id = #{id}

SELECT
FROM wy_biz_def a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM wy_biz_def a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE wy_biz_def SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
