mapper.select:
SELECT 

FROM wx_msg_tpl a

WHERE a.id = #{id}

SELECT 

FROM wx_msg_tpl a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.account_id = #{accountId}




		ORDER BY ${page.orderBy}


		ORDER BY c.code ASC

SELECT 

FROM wx_msg_tpl a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY c.code ASC
