mapper.select:
SELECT 

FROM wx_news a

WHERE a.id = #{id}

SELECT 

FROM wx_news a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.account_id = #{account.id}


		AND a.keyword LIKE 
			'%'||#{keyword}||'%'
			'%'+#{keyword}+'%'
			concat('%',#{keyword},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM wx_news a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.account_id = #{account.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE wx_news SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT 

FROM wx_news a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.account_id = #{account.id}


		AND a.id EXISTS (${keyword})
