mapper.select:
SELECT 

FROM wx_news_multi a

WHERE a.id = #{id}

SELECT 

FROM wx_news_multi a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.account_id = #{account.id}


		AND a.keywords LIKE 
			'%'||#{keywords}||'%'
			'%'+#{keywords}+'%'
			concat('%',#{keywords},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM wx_news_multi a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE wx_news_multi SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
