mapper.select:
SELECT 

FROM wy_house_rent a

WHERE a.id = #{id}

SELECT 

FROM wy_house_rent a

WHERE a.audit =1

	AND (a.title LIKE CONCAT('%',#{title},'%') OR a.house_desc LIKE CONCAT('%',#{title},'%'))					


	AND a.rental_type = #{rentalType}


	AND a.member_id = #{memberId}


	AND a.source = #{source}


	AND a.group_id = #{groupId}


	AND a.appid = #{appid}

ORDER BY a.update_date DESC

mapper.update:
UPDATE wy_house_rent SET 	

		comment_num = #{commentNum}

WHERE id = #{id}
